package Vererbung.InterfacesAbstrakteKlassenPolymorphieZahlRaten;public class CombinedIntOperation implements IntOperation {    private IntOperation firstOp;    private IntOperation secondOp;    public CombinedIntOperation(IntOperation firstOp, IntOperation secondOp) {        this.firstOp = firstOp;        this.secondOp = secondOp;    }    @Override    public int execute(int value) {        int tmp = firstOp.execute(value);        return secondOp.execute(tmp);    }}